function invertieren() {
  A=eval(document.getElementById("matrix input").value);
  n=A.length;
  NeuerNenner=1;
  for (i=0;i<n;i++) {
    //alert("i="+i);
    AlterNenner=NeuerNenner;
    NeuerNenner=A[i][i];Y=[];Z=[];
    for (j=0;j<n;j++) {
      Y[j]=A[j][i];
      Z[j]=A[i][j];
      }
    Y[i]=Y[i]+AlterNenner;
    Z[i]=Z[i]-AlterNenner;
    for (j=0;j<n;j++) { for (k=0;k<n;k++) { 
      A[j][k]=(A[j][k]*NeuerNenner-Y[j]*Z[k])/AlterNenner;
      } }
    alert("i="+i+"NeuerNenner="+NeuerNenner+" AlterNenner="+AlterNenner+" Y="+Y+" Z="+Z+" A=("+A+")/"+NeuerNenner);
    }
  }
